@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.carousel {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin-bottom: 6.25rem;

  @include responsive(tablet) {
    // gap: 3.5rem;
    margin-bottom: 7.5rem;
  }

  @include responsive(desktop) {
    margin-bottom: 5.5rem;
  }

  .slider-container {
    // overflow: hidden;
    overflow-x: scroll;
    max-width: fit-content;
    margin: 0 auto;

    .slider {
      list-style: none;
      padding: 0;
      display: flex;
      align-items: center;
      justify-content: flex-start;
      // gap: 0.9375rem;
      gap: 32px;
      height: 17.5rem;
      // overflow-x: scroll;
      position: relative;

      @include responsive(phone-lg) {
        height: 23.75rem;
        // gap: 1.875rem;
      }

      .slides {
        flex: 0 0 auto;
        max-width: 100%;
        display: inline-block;
        white-space: nowrap;
        // position: absolute;
        transition: transform 1s;

        .slides-img {
          border-radius: 0.625rem;
        }
      }
    }
  }
}

