@use "../abstracts/variables" as *;
@use "../abstracts/mixins" as *;

.intro {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  max-width: 69.375rem;
  margin: 0 auto 2rem;
  padding: 0 $sides-small;

  @include responsive(tablet) {
    margin-bottom: 4rem;
    padding: 0 $sides-medium;
  }

  @include responsive(desktop) {
    margin-bottom: 5rem;
  }
}

.author-container {
  max-width: 69.375rem;
  padding: 0 $sides-small;
  margin: 0 auto 6.25rem;

  @include responsive(tablet) {
    padding-right: 1rem;
    padding-left: 0;
    margin-bottom: 7.5rem;
  }

  @include responsive(desktop) {
    gap: 7.8125rem;
    padding: 0 2.5rem;
    margin-bottom: 5.5rem;
  }

  .author {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    gap: 2.5rem;

    @include responsive(tablet) {
      flex-direction: row;
      gap: 4.3125rem;
      justify-content: center;
      margin-left: -3.125rem;
    }

    @include responsive(desktop) {
      gap: 7.8125rem;
      margin-left: 0;
    }

    .bio {
      display: flex;
      align-items: center;
      flex-direction: column;
      gap: 1.5rem;

      @include responsive(tablet) {
        text-align: left;
        align-items: flex-start;
      }

      @include responsive(desktop) {
        gap: 2rem;
      }
    }

    .author-img-container {
      max-width: 27.8125rem;
    }
  }
}

.contact-container {
  max-width: 69.375rem;
  margin: 0 auto 2.5rem;
  padding: 0 $sides-small;

  @include responsive(tablet) {
    padding: 0 $sides-medium;
    margin: 0 auto 3.5rem;
  }

  .contact-wrap {
    background: $black;
    text-align: center;
    border-radius: 0.625rem;

    @include responsive(desktop) {
      padding-inline: 1.25rem;
    }
  }

  .contact {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    padding: 3rem 1.5rem;

    @include responsive(tablet) {
      gap: 1.625rem;
      padding-block: 2.5rem;
      padding-top: 4.6875rem;
      padding-bottom: 4.6875rem;
    }

    @include responsive(desktop) {
      flex-direction: row;
      justify-content: space-between;
      gap: 3.25rem;
      padding-block: 2.5rem;
      padding-top: 5rem;
      padding-bottom: 5rem;
    }

    .contact-text {
      display: flex;
      align-items: center;
      flex-direction: column;
      gap: 1.5rem;

      @include responsive(tablet) {
        gap: 1.625rem;
      }

      @include responsive(desktop) {
        gap: 1.5625rem;
        text-align: left;
        align-items: flex-start;
        width: 50%;
      }
    }
  }
}
